// allows for use of built in React function - useState
import { useState } from "react";
// creates a reusable square prop that can be accessed in file
// { value } allows to pass property 'value' and is accessed with {value}
function Square() {
  // 'value' stores the value and 'setValue' is a function that can be used to change value
  // 'null' is the initial value for this state value
  const [value, setValue] = useState(null);

  // default square has attached interaction component
  function handleClick() {
    // when clicked, calls setValue() 
    setValue('X');
  }

  return <button className="square" onClick={handleClick}>{value}</button>;
}
// renders tic-tac-toe board
export default function Board() {
  // returns three seperate rows with identical className
  return (
    <>
    {/* <Square /> accesses function Square for default square prop */}
      <div className="board-row">
        {/* value = "" is passing the desired value */}
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
    </>
  );
}
